document.addEventListener('DOMContentLoaded',function(){console.log('Dropdown fix loaded');function initializeDropdowns(){document.querySelectorAll('.dropdown-toggle').forEach(element=>{element.removeEventListener('click',handleDropdownClick)});document.querySelectorAll('.dropdown-toggle').forEach(element=>{element.addEventListener('click',handleDropdownClick)});const userAvatar=document.querySelector('.user-avatar.dropdown-toggle');if(userAvatar){userAvatar.style.cursor='pointer';userAvatar.removeEventListener('click',handleDropdownClick);userAvatar.addEventListener('click',handleDropdownClick)}}function handleDropdownClick(event){event.preventDefault();event.stopPropagation();const element=this;console.log('Dropdown clicked',element);document.querySelectorAll('.dropdown.show').forEach(dropdown=>{if(dropdown!==element.closest('.dropdown')){dropdown.classList.remove('show')}});const dropdownElement=element.closest('.dropdown');if(dropdownElement){dropdownElement.classList.toggle('show');const menu=dropdownElement.querySelector('.dropdown-menu');if(menu&&dropdownElement.classList.contains('show')){menu.style.display='block';menu.style.visibility='visible';menu.style.opacity='1';menu.style.pointerEvents='auto';menu.style.zIndex='9999'}}}document.addEventListener('click',function(event){if(!event.target.closest('.dropdown')){document.querySelectorAll('.dropdown.show').forEach(dropdown=>{dropdown.classList.remove('show');const menu=dropdown.querySelector('.dropdown-menu');if(menu){menu.style.display='';menu.style.visibility='';menu.style.opacity='';menu.style.pointerEvents=''}})}});initializeDropdowns();const observer=new MutationObserver(function(mutations){initializeDropdowns()});const header=document.querySelector('header');if(header){observer.observe(header,{childList:true,subtree:true})}});