document.addEventListener('DOMContentLoaded',function(){setTimeout(function(){console.log('Initializing simple dropdown fix...');const userAvatarLinks=document.querySelectorAll('.user-avatar,.notification-item a');userAvatarLinks.forEach(function(avatar){if(!avatar)return;console.log('Setting up avatar click:',avatar);avatar.addEventListener('click',function(e){e.preventDefault();e.stopPropagation();console.log('Avatar clicked');let dropdownMenu=null;const dropdownContainer=avatar.closest('.dropdown');if(dropdownContainer){dropdownMenu=dropdownContainer.querySelector('.dropdown-menu')}if(!dropdownMenu){console.log('No dropdown menu found');return}console.log('Found dropdown menu:',dropdownMenu);const allDropdowns=document.querySelectorAll('.dropdown-menu');allDropdowns.forEach(function(menu){if(menu!==dropdownMenu){menu.style.display='none';menu.classList.remove('show')}});const isVisible=dropdownMenu.style.display==='block'||dropdownMenu.classList.contains('show');if(isVisible){dropdownMenu.style.display='none';dropdownMenu.classList.remove('show');console.log('Dropdown closed')}else{dropdownMenu.style.display='block';dropdownMenu.classList.add('show');console.log('Dropdown opened')}})});document.addEventListener('click',function(e){if(!e.target.closest('.dropdown')){const allDropdowns=document.querySelectorAll('.dropdown-menu');allDropdowns.forEach(function(menu){menu.style.display='none';menu.classList.remove('show')})}});document.addEventListener('keydown',function(e){if(e.key==='Escape'){const allDropdowns=document.querySelectorAll('.dropdown-menu');allDropdowns.forEach(function(menu){menu.style.display='none';menu.classList.remove('show')})}})},500)});