document.addEventListener("DOMContentLoaded",function(){console.log("DOM is ready");const regionLinks=document.querySelectorAll(".region-link");regionLinks.forEach(function(regionLink){regionLink.addEventListener("click",function(event){event.preventDefault();const clickedRegion=regionLink.closest(".region");fetch("areas.php?region_id="+regionLink.getAttribute("data-region-id")).then((response)=>response.text()).then((data)=>{clickedRegion.innerHTML+=data;regionLink.classList.add("disabled");const allRegions=document.querySelectorAll(".region");allRegions.forEach(function(otherRegion){if(otherRegion!==clickedRegion){otherRegion.classList.toggle("d-none");console.log("Hidden class toggled")}});const showAllRegionsLink=document.getElementById("showAllRegionsLink");showAllRegionsLink.classList.remove("d-none");const heading=document.querySelector(".text-5xl.font-bold.mb-4.text-center");heading.classList.add("d-none")}).catch((error)=>console.error("Error fetching areas:",error))})});const showAllRegionsLink=document.getElementById("showAllRegionsLink");showAllRegionsLink.addEventListener("click",function(event){event.preventDefault();const clickedRegion=document.querySelector(".region:not(.d-none)");clickedRegion.innerHTML="";regionLinks.forEach(function(link){link.classList.remove("disabled")});const allRegions=document.querySelectorAll(".region");allRegions.forEach(function(region){region.classList.remove("d-none")});showAllRegionsLink.classList.add("d-none");const heading=document.querySelector(".text-5xl.font-bold.mb-4.text-center");heading.classList.remove("d-none")})});